INSERT INTO documents (creation_date, description, document_url, image_url, id) VALUES ('19 листопада 2022', 'Катехизм “Христос — наша Пасха” — офіційний текст віровчення Української Греко-Католицької Церкви. Книга розкриває особливість богословської традиції УГКЦ, спираючись на Святе Письмо, спадщину Отців Церкви, душпастирів УГКЦ, рішень Вселенських та Помісних Соборів, богослужбових творів, та ін. Офіційне представлення катехизму відбулось у Львівській духовній семінарії Святого Духа 24 червня 2011 року.', '/public/file/PEvqqkntcFhFJ8CMbanX.pdf', '/public/images/fzzZCj3JRDUCy6mK10bR.png', 85);
INSERT INTO documents (creation_date, description, document_url, image_url, id) VALUES ('19 листопада 2022', 'Катехизм Католицької Церкви (ККЦ) — книга, що містить стислий виклад основних положень католицької віри. Найновіший католицький катехизм був укладений за постановою позачергового Синоду єпископів 1985 року. Папа Йоан Павло II затвердив його 25 червня 1992 року. Як зазначено в передмові, цей катехизм містить «і нове, і старе (пор. Мт. 13,52), оскільки віра завжди залишається тією самою; і водночас вона є джерелом завжди нового світла». ', '/public/file/kX9yqZ8QMEj7iYLXZ0Ez.pdf', '/public/images/A4Ql7MiV241EZs5EGg2C.jpeg', 86);
INSERT INTO documents (creation_date, description, document_url, image_url, id) VALUES ('19 листопада 2022', 'Катехизм “Христос — наша Пасха” — офіційний текст віровчення Української Греко-Католицької Церкви. Книга розкриває особливість богословської традиції УГКЦ, спираючись на Святе Письмо, спадщину Отців Церкви, душпастирів УГКЦ, рішень Вселенських та Помісних Соборів, богослужбових творів, та ін. Офіційне представлення катехизму відбулось у Львівській духовній семінарії Святого Духа 24 червня 2011 року.', '/public/file/PEvqqkntcFhFJ8CMbanX.pdf', '/public/images/fzzZCj3JRDUCy6mK10bR.png', 87);
INSERT INTO documents (creation_date, description, document_url, image_url, id) VALUES ('19 листопада 2022', 'Катехизм Католицької Церкви (ККЦ) — книга, що містить стислий виклад основних положень католицької віри. Найновіший католицький катехизм був укладений за постановою позачергового Синоду єпископів 1985 року. Папа Йоан Павло II затвердив його 25 червня 1992 року. Як зазначено в передмові, цей катехизм містить «і нове, і старе (пор. Мт. 13,52), оскільки віра завжди залишається тією самою; і водночас вона є джерелом завжди нового світла». ', '/public/file/kX9yqZ8QMEj7iYLXZ0Ez.pdf', '/public/images/A4Ql7MiV241EZs5EGg2C.jpeg', 88);